mapper.select:
update Book set TITLE=#{title,jdbcType=VARCHAR},AUTHOR=#{author,jdbcType=VARCHAR},ISBN=#{bookIsbn,jdbcType=VARCHAR},
QUANTITY=#{quantity,jdbcType=INTEGER},PRICE=#{price,jdbcType=DOUBLE},PUBLISHER=#{publisher,jdbcType=VARCHAR},
PUBLISH_TIME=#{publishTime,jdbcType=DATE} where BOOK_ID=#{bookId,jdbcType=INTEGER}
